INSERT INTO `notification` (`id`, `type`, `object_id`, `user_id`, `body`, `url`, `read`, `created_at`, `updated_at`, `subject`, `object_type`, `sent_at`, `sender_id`, `deleted_at`) VALUES
(2, 'message', 0, 1, '<div class=\"item_noify\">\r\n	<p class=\"welcome\"><strong>{name}</strong> thân mến.</p>\r\n	<p>Xin chúc mừng bạn đã trở thành giảng viên tại myedu.</p>\r\n	<p>\r\n		Hãy tạo khóa học đầu tiên để chia sẻ tới cộng đồng, chúng tôi sẽ đem đến cho bạn 10 triệu học viên, thu nhập, danh tiếng và hơn thế nữa. \r\n	</p>\r\n	<p>Tạo khóa học đầu tiên <a style=\"color: #ffaa00;text-decoration: none;\" href=\"http://myedu.io/teacher/add_new_course\" rel=\"nofollow\">tại đây</a>.</p>\r\n	<p>Trân trọng.</p>\r\n\r\n</div>', '', 1, '2016-09-13 00:17:19', '2016-09-13 00:17:47', 'Xin chúc mừng bạn đã trở thành giảng viên tại Quochoc.vn', '', '2016-09-13 14:17:19', 1, NULL),
(3, 'message', 0, 3, '<div class=\"item_noify\">\r\n	<p class=\"welcome\"><strong>{name}</strong> thân mến.</p>\r\n	<p>\r\n		Bạn đã đăng ký trở thành giảng viên tại myedu thành công. Chúng tôi sẽ duyệt yêu cầu của bạn trong 2 ngày làm việc. \r\n		Nếu sau 2 ngày làm việc, bạn không nhận được bất cứ phàn hồi nào từ chúng tôi thì vui lòng liên hệ tới BQT để được hỗ trợ. \r\n	</p>\r\n	<p>Trân trọng.</p>\r\n\r\n</div>', '', 1, '2016-09-13 02:16:12', '2016-09-14 03:44:29', 'Bạn đã đăng ký trở thành giảng viên tại myedu thành công. Chúng tôi sẽ duyệt yêu cầu của bạn trong 2 ngày làm việc.', '', '2016-09-13 16:16:12', 1, NULL),
